Handlebars.registerHelper('formatDate', function(ts) {
  if (typeof ts == 'string') ts = parseInt(ts, 10);

  var date = new Date(ts),
    array = date.toUTCString().split(' '),
    day = parseInt(array[1]),
    daySuffix = 'th';

  switch (day % 10) {
    case 1:
    case 21:
    case 31:
      daySuffix = 'st';
      break;
    case 2:
    case 22:
      daySuffix = 'nd';
      break;
    case 3:
    case 23:
      daySuffix = 'rd';
      break;
    default:
      daySuffix = 'th';
  }

  return (array[2] + ' ' + array[1] + daySuffix + ' ' + array[3]);
});

