function serializeList(container) {
  var str = '',
    els = container.find('li');
  for (var i = 0; i < els.length; ++i) {
    var el = els[i],

      p = el.id.lastIndexOf('_'),
      getIdNumber = el.id.split("_");

    if (p !== -1) {
      if (str !== '') str = str + '&';
      str = str + el.id.substring(0, p) + '[]=' + getIdNumber[1];
    }
  }
  return str;
}

